svg.script:
 var doc, svg, left, right;

 function onLoad(evt) {
 	svg = evt.target;
 	doc = svg.ownerDocument;
 	left = doc.getElementById("left");
 	right = doc.getElementById("right");
	left.setAttribute("fill", "white");
 }

 function onResize(evt) {
 	var width = parseInt(svg.getAttribute("width"));
 	var height = parseInt(svg.getAttribute("height"));

	var value = $model.value;

	var barHeight = height - 4;
	var barWidth = Math.round(width / 3);
	var dx = Math.round((width - barWidth) * value / 100);

	left.setAttribute("x", dx);	
	left.setAttribute("width", barWidth);	
	left.setAttribute("height", barHeight);
	right.setAttribute("x", 0);	
	right.setAttribute("width", width);	
	right.setAttribute("height", barHeight);
 }
